import React from 'react'; 
import NavBar from './NavBar';
import Job from './Job';
import {Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Row, Col} from 'reactstrap'
import "./index.css";


class Driver extends React.Component{

    constructor(props) {
        super(props);
        this.state={
            showJobs: false
        }
    }

    toggle() {
        if(this.state.showJobs) {
            this.setState({
                showJobs: false
            });
        } else {
            this.setState({
                showJobs: true
            });
        }
    }

    renderButton() {
        return( 
            <Row>
                <Col sm={{offset:5}}>
                    <Dropdown isOpen={false} toggle={this.toggle}>
                        <DropdownToggle caret>
                            3 Recylcing Job's Available
                        </DropdownToggle>
                        <DropdownMenu>
                            <DropdownItem>
                                <Job number='1' address='12345 Aggie Lane, College Station, TX'/>
                            </DropdownItem>
                            <DropdownItem>
                                <Job number='2' address='54321 TAMU Street, College Station, TX' />
                            </DropdownItem>
                            <DropdownItem>
                                <Job number='3' address='00000 Yes Circle, Bryan, TX, College Station, TX' />
                            </DropdownItem>
                        </DropdownMenu>
                    </Dropdown>
                </Col>
            </Row>
        );
    }

    toggle = this.toggle.bind(this);

    renderResults() {
        if(!this.state.showJobs) {
            return <div />
        } else {
            return (
                <div>

                </div>
            );
        }
    }

    render(){
        return(
            <div>
                <NavBar/>
                {this.renderButton()}
                {this.renderJobs()}
            </div>
        )

    }
}

export default Driver;
